import styled from 'styled-components'
import Image from 'next/image'
import Theme from '../../public/theme'
import Button from '../components/button'
import Container from '../components/container'

const CheckList = () => {

    return (
        <Container
            mediaOptions=" order: 1; flex-direction: row;"
            align="center"
            column
        >
            <ContainerImage>
                <Image
                    src="/coding.png"
                    width={500}
                    height={500}
                />
            </ContainerImage>
            <ul style={{ listStyle: 'none', padding: 0 }}>
                <ListElement>Domínio personalizado</ListElement>
                <ListElement>Hospedagem</ListElement>
                <ListElement>Site responsivo</ListElement>
                <ListElement>Redes sociais</ListElement>
                <ListElement>Chat online</ListElement>
                <ListElement>Blog integrado</ListElement>
            </ul>

            <Container column justify="space-between" align="stretch" style={{ height: '200px' }}>
                <text style={{ fontWeight: 'bolder', fontSize: 25 }}>
                    Nossa equipe está à disposição!
                    </text>
                <br />
                <Input placeholder="Celular" />
                <br />
                <Button mediaWidth="45%">
                    Ligamos para você
                        </Button>
            </Container>
        </Container>
    )

}

const ContainerImage = styled.div`
    max-width: 300px;
    max-height: 300px;

    @media(min-width: 720px) {
        order: 2;
    }
`

const ListElement = styled.li`
    font-size: 1.3em;
    font-weight: bold;

    &:not(:last-child) {
        margin-bottom: 10px;
    }

    &::before {
        font-size: 1.5em;
        margin-right: 10px;
        content: "✓";
        color: white;
        background-color: ${Theme.colors.primary};
        padding: 0 3px;
        border-radius: 100%;
    }

`
const Input = styled.input`
    padding: 10px 15px;
    background-color: ${Theme.colors.grey};
    outline: none;
    border: none;
    border-radius: 5px;
`

export default CheckList
