import styled from 'styled-components'
import Container from './container'

const ContainerText = ({ title, text }) => {

    return (

        <Container style={{ padding: '0px 20px' }} column align="flex-start">
            <SubTitle>
                {title}
            </SubTitle>

            <text style={{ color: 'white' }}>{text}</text>
        </Container>
    )
}

const Footer = () => {

    const texts = [
        {
            title: "Sobre",
            text: "Amamos criar soluções e entregar sites incríveis"
        },
        {
            title: "Contato",
            text: <>
                <text>123 Av. Manjar - Nárnia </text><br />
                <text>+55 31 97070-7070</text><br />
                <text>contato@contato.com</text>
            </>,
        },
        {
            title: "Redes Sociais",
            text: <>
                <i className="fab fa-instagram" color="white">teste</i>
                <i color="white">Teste 2</i>
            </>
        }
    ]


    return (
        <Container style={{ backgroundColor: 'black', marginTop: 100, padding: '20px' }}>
            <Container>
                <Title>Codar</Title>
                <text>Criamos websites incríveis</text>
            </Container>

            {texts.map((item) =>
                <ContainerText
                    text={item.text}
                    title={item.title}
                />
            )}

        </Container>
    )
}

export default Footer

const Title = styled.text`
    font-size: 1.5em;
    color: white;
    font-weight: bolder;

    &:after {
        content: '.';
        font-size: 2em;
    }
`

const SubTitle = styled.text` 
    color: white;
    font-size: 1.2em;
    font-weight: bolder;
`
