import styled from 'styled-components'
import Container from './container'

const ContainerText = ({ title, text }) => {

    return (

        <Container>
            <SubTitle>
                {title}
            </SubTitle>

            <text>{text}</text>
        </Container>
    )
}

const Footer = () => {

    return (
        <Container style={{ backgroundColor: 'black', marginTop: 100, padding: '20px' }}>
            <Container>
                <Title>Codar</Title>
                <text>Criamos websites incríveis</text>
            </Container>

            <ContainerText
                title="Sobre"
                text="Amamos criar soluções e entregar sites incríveis" />
        </Container>
    )
}

export default Footer

const Title = styled.text`
    font-size: 1.5em;
    color: white;
    font-weight: bolder;

    &:after {
        content: '.';
        font-size: 2em;
    }
`

const SubTitle = styled.text` 
    color: white;
    font-size: 1.2em;
    font-weight: bolder;
`
