import styled from 'styled-components'
import Theme from '../../public/theme'
import Container from './container'

const ContainerText = ({ title, text }) => {

    return (

        <Container style={{ padding: '0px 20px', border: '1px solid white' }} column align="flex-start">
            <SubTitle>
                {title}
            </SubTitle>

            <text style={{ color: 'white' }}>{text}</text>
        </Container>
    )
}

const Footer = () => {

    const texts = [
        {
            title: "Sobre",
            text: <p>Amamos criar soluções e entregar sites incríveis</p>
        },
        {
            title: "Contato",
            text: <>
                <p>123 Av. Manjar - Nárnia </p>
                <p>+55 31 97070-7070</p>
                <p>contato@contato.com</p>
            </>,
        },
        {
            title: "Redes Sociais",
            text: <Container justify="space-evenly" flex="1">
                <p><Icon className="fab fa-instagram" color="white"></Icon></p>
                <p><Icon className="fab fa-facebook-f" ></Icon></p>
                <p><Icon className="fab fa-twitter" ></Icon></p>
            </Container>
        }
    ]


    return (
        <Container style={{ backgroundColor: 'black', marginTop: 100, padding: '20px' }}>
            <Container>
                <Title>Codar</Title>
                <p>Criamos websites incríveis</p>
            </Container>

            {texts.map((item) =>
                <ContainerText
                    key={item.title}
                    text={item.text}
                    title={item.title}
                />
            )}

        </Container>
    )
}

export default Footer

const Title = styled.text`
    font-size: 1.5em;
    color: white;
    font-weight: bolder;

    &:after {
        content: '.';
        font-size: 2em;
    }
`

const SubTitle = styled.text` 
    color: white;
    font-size: 1.2em;
    font-weight: bolder;
`

const Icon = styled.i`
    color: ${Theme.colors.primary};
    font-size: 1.5em;
    cursor: pointer;
    transition: 0.5s all;

    &:hover {
        color: white;
    }
`
