import styled from 'styled-components'
import Image from 'next/image'
import Theme from '../../public/theme'

const CheckList = () => {

    return (
        <Container style={{ marginTop: 100 }}>
            <ContainerImage>
                <Image
                    src="/coding.png"
                    width={500}
                    height={500}
                />
            </ContainerImage>

            <Container style={{ marginTop: 50 }}>
                <ul style={{ listStyle: 'none' }}>
                    <ListElement>Domínio personalizado</ListElement>
                    <ListElement>Hospedagem</ListElement>
                    <ListElement>Site responsivo</ListElement>
                    <ListElement>Redes sociais</ListElement>
                    <ListElement>Chat online</ListElement>
                    <ListElement>Blog integrado</ListElement>
                </ul>
            </Container>
        </Container>
    )

}

const Container = styled.div` 
    display: flex;
    flex: 1;
    flex-direction: column;
    align-items: center;
`

const ContainerImage = styled.div`
    height: 60%;
    width: 60%;
    max-width: 300px;
    max-height: 300px;
`
const ListElement = styled.li`
    font-size: 1.3em;
    font-weight: bold;

    &:not(:last-child) {
        margin-bottom: 10px;
    }

    &::before {
        font-size: 1.5em;
        margin-right: 20px;
        content: "✓";
        color: white;
        background-color: ${Theme.colors.primary};
        border-radius: 50%;
    }

`


export default CheckList
